import axios from 'axios';
import React, { useEffect, useState, useRef } from 'react';
import VideosContainer from "./VideosWrapper"

function useForceUpdate(){
  const [value, setValue] = useState(0); // integer state
  return () => setValue(value => value + 1); // update the state to force render
}

const History: React.FC = () => {
  const [episodeNumber, setEpisodeNumber] = useState<number>(0);
  const forceUpdate = useForceUpdate();
  var videoList = [<></>];
  useEffect(()=>{
    console.log('mount');
    axios.get(`/episode_number`)
      .then((res) => {
        console.log(res.data);
        setEpisodeNumber(res.data.episode_number);
        forceUpdate();
      });
  },[]);

  useEffect(()=>{
    videoList = episodeNumber > 0 ? [...Array(episodeNumber-1)].map((x, i) =>
      <VideosContainer idx={i+1}/>
    ) : [<></>];
  },[episodeNumber]);

  console.log(episodeNumber, videoList);
  return (
    <div className="mx-auto mt-10">
      <div className="flex flex-col overflow-auto justify-items-center align-center h-4/5 px-6">
        {videoList}
      </div>
    </div>
  );
};

export default History;
