import axios from 'axios';
import React, { useEffect, useState, useRef } from 'react';
import { LazyLoadComponent } from 'react-lazy-load-image-component';

const firstChoiceStyle = [
  "outline outline-offset-0 outline-green-500",
  "outline outline-offset-0 outline-red-500",
  "outline outline-offset-0 outline-green-300",
  "outline outline-offset-0 outline-black-500"
];

const secondChoiceStyle = [
  "outline outline-offset-0 outline-green-500",
  "outline outline-offset-0 outline-red-500",
  "outline outline-offset-0 outline-green-300",
  "outline outline-offset-0 outline-black-500"
];

const VideosWrapper: React.FC<{idx:number}> = ({ idx }) => {
  const [display, setDisplay] = useState<boolean>(false); 
  const [choice, setChoice] = useState<number>(-1);
  useEffect(()=>{
    axios.get(`/videos/video_one_${idx}.mp4`)
      .then(() => {
        setDisplay(true);
      })
      .catch(()=>{
        setDisplay(false);
      });
  },[]);
  useEffect(()=>{
    axios.get(`/choice/${idx}`)
      .then((res) => {
        setChoice(res.data.choice);
      })
      .catch(()=>{
        setDisplay(false);
      });
  },[]);

  return (
    <>
      {display ?
        <LazyLoadComponent threshold={20}>
          <div className="flex flex-row my-4">
            <div className="flex flex-col">
              <div className="flex flex-row mx-auto">
                Episode {idx}
              </div>
              <div className="flex flex-row">
                <video className={`rounded-xl mx-4 ${firstChoiceStyle[choice]}`} autoPlay loop muted width="140" height="140" src={`videos/video_one_${idx}.mp4`}/>
                <video className={`rounded-xl mx-4 ${secondChoiceStyle[choice]}`} autoPlay loop muted width="140" height="140" src={`videos/video_two_${idx}.mp4`}/>
              </div>
            </div>
          </div>
        </LazyLoadComponent>
        :
        <></>
      }
    </>
  );
};

export default VideosWrapper;
