import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SampleChoice: React.FC = () => {
  const [episodeNumber, setEpisodeNumber] = useState<number>(1);
  const [nextEpisodeAvailable, setNextEpisodeAvailable] = useState<boolean>(false);
  const submitChoice = (choice: number) => {
    // axios.get(`/episode_number`)
    //   .then(res => {
    //     const episode_number = res.data.episode_number;
    //     console.log(res.data, episode_number)
    //     setEpisodeNumber(episode_number);
    //   });
    axios.post(`/human_choice/${choice}`)
      .then(res => {
        setEpisodeNumber(res.data.displayed_episode);
      });
  };
  const getNextEpisode = () => {
    axios.get(`/episode_number`)
      .then(res => {
        const episode_number = res.data.episode_number;
        console.log(res.data, episode_number)
        setEpisodeNumber(episode_number);
      });
  };

  return (
    <>
      {nextEpisodeAvailable ? 
        <div className="flex flex-row mx-auto mt-40">
          <div className="flex flex-col mx-4">
            <video autoPlay loop muted width="320" height="320" src={`videos/video_one_${episodeNumber}.mp4`}/>
            <button className="bg-amber-400 hover:bg-amber-500 active:bg-amber-600" onClick={()=>(submitChoice(1))}>1</button>
            <button className="bg-amber-400 hover:bg-amber-500 active:bg-amber-600" onClick={()=>(submitChoice(3))}>Equally good</button>
          </div>
          <div className="flex flex-col mx-4">
            <video autoPlay loop muted width="320" height="320" src={`videos/video_two_${episodeNumber}.mp4`}/>
            <button className="bg-amber-400 hover:bg-amber-500 active:bg-amber-600" onClick={()=>(submitChoice(2))}>2</button>
            <button className="bg-amber-400 hover:bg-amber-500 active:bg-amber-600" onClick={()=>(submitChoice(4))}>Discard</button>
          </div>
          {/* <button className="bg-amber-400 hover:bg-amber-500 active:bg-amber-600" onClick={()=>(getNextEpisode())}>Get next episode</button> */}
        </div>
      :
        <div>
          <button></button>
        </div>
      }
    </>
  );
}

export default SampleChoice;
